```language-php

<?php
// Old/Current behavior vs. New/Desired behavior

/**
 * This is the old way. Using an ugly regexes. Bad developer!
 */
$rules = [
    'email'    => "/\w+/",
    'password' => "/\w+/"
];

/**
 * This is what we want. An array of readable/sensible validation rules. Better developer!
 */
